import React from "react";
import { Button, Form, Table } from "react-bootstrap";

export const TaskList = ({
  title,
  arrow,
  list = [],
  switchTask,
  handleOnCheck,
  name,
  ids,
}) => {
  return (
    <div className="mt-3">
      <h2 className="text-center">{title}</h2>
      <div className="table mt-4">
        <Table striped>
          <thead>
            <tr>
              <th>
                <Form.Check
                  type="checkbox"
                  value={name}
                  onChange={handleOnCheck}
                />
              </th>
              <th>Task</th>
              <th>Hours</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {list.map((item, i) => (
              <tr>
                <td>
                  <Form.Check
                    type="checkbox"
                    value={item.id}
                    checked={ids.includes(item.id)}
                    onChange={handleOnCheck}
                  />
                </td>
                <td>{item.task}</td>
                <td>{item.hr}hrs</td>
                <td>
                  {arrow === "right" ? (
                    <Button
                      variant="success"
                      onClick={() => switchTask(item.id, "bad")}
                    >
                      <i class="fa-solid fa-arrow-right"></i>
                    </Button>
                  ) : (
                    <Button
                      variant="danger"
                      onClick={() => switchTask(item.id, "entry")}
                    >
                      <i class="fa-solid fa-arrow-left"></i>
                    </Button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </div>
    </div>
  );
};
